  tags = {
    Name = "high-throughput-volume"
  }
}

resource "aws_s3_bucket" "sqs_notifications" {
  bucket = "my-sqs-notifications-bucket"
}

resource "aws_sqs_queue" "notifications" {
  name = "s3-notifications-queue"
}

resource "aws_s3_bucket_notification" "sqs_notifications" {
  bucket = aws_s3_bucket.sqs_notifications.id

  queue {
    queue_arn = aws_sqs_queue.notifications.arn
