  bucket = "my-inventory-bucket"
}

resource "aws_s3_bucket" "destination" {
  bucket = "inventory-destination"
}

resource "aws_s3_bucket_inventory" "inventory" {
  bucket = aws_s3_bucket.inventory.id
  name   = "entire-bucket-inventory"

  included_object_versions = "All"

  schedule {
    frequency = "Weekly"
  }

  destination {
    bucket {
      bucket_arn = aws_s3_bucket.destination.arn
      format      = "CSV"
      prefix      = "inventory"
    }
  }
}

resource "aws_s3_bucket" "analytics" {
  bucket = "my-analytics-bucket"
}

resource "aws_s3_bucket_analytics_configuration" "analytics" {
  bucket = aws_s3_bucket.analytics.id
  name   = "entire-bucket-analytics"

  storage_class_analysis {
    data_export {
      destination {
        s3_bucket_destination {
          bucket_arn = aws_s3_bucket.destination.arn
          format     = "CSV"
          prefix     = "analytics"
        }
      }
    }
  }
}

resource "aws_s3_bucket" "object_lock" {
  bucket = "my-object-lock-bucket"

  object_lock_enabled = true
}

resource "aws_s3_bucket_object_lock_configuration" "object_lock" {
  bucket = aws_s3_bucket.object_lock.id

  rule {
    default_retention {
      mode = "GOVERNANCE"
      days = 30
    }
  }
}

resource "aws_s3_bucket" "cors" {
  bucket = "my-cors-bucket"
}

resource "aws_s3_bucket_cors_configuration" "cors" {
  bucket = aws_s3_bucket.cors.id

  cors_rule {
    allowed_headers = ["*"]
    allowed_methods = ["GET", "PUT", "POST"]
    allowed_origins = ["https://example.com"]
    expose_headers  = ["ETag"]
    max_age_seconds = 3000
  }
}

resource "aws_s3_bucket" "lifecycle" {
  bucket = "my-lifecycle-bucket"
}

resource "aws_s3_bucket_lifecycle_configuration" "lifecycle" {
  bucket = aws_s3_bucket.lifecycle.id

  rule {
    id     = "archive-old-files"
    status = "Enabled"

    expiration {
      days = 90
    }

    transition {
      days          = 30
      storage_class = "STANDARD_IA"
    }
  }
}

resource "aws_s3_bucket" "eventbridge" {
  bucket = "my-eventbridge-bucket"
}

resource "aws_s3_bucket_notification" "eventbridge" {
  bucket = aws_s3_bucket.eventbridge.id

  eventbridge = true
}

resource "aws_kms_key" "ebs" {
  description             = "KMS key for EBS encryption"
  deletion_window_in_days = 10
}

resource "aws_ebs_volume" "encrypted_kms" {
  availability_zone = "us-east-1a"
  size              = 100
  type              = "gp3"
  encrypted         = true
  kms_key_id        = aws_kms_key.ebs.arn

  tags = {
    Name = "encrypted-kms-volume"
  }
}

resource "aws_ebs_volume" "multi_attach" {
  availability_zone = "us-east-1a"
