  target_prefix = "log/"
}

resource "aws_s3_bucket" "multi_events" {
  bucket = "my-multi-events-bucket"
}

resource "aws_s3_bucket_notification" "multi_events" {
  bucket = aws_s3_bucket.multi_events.id

  lambda_function {
    lambda_function_arn = aws_lambda_function.processor.arn
    events              = ["s3:ObjectCreated:*", "s3:ObjectRemoved:*"]
  }
}

resource "aws_s3_bucket" "secure" {
  bucket = "my-secure-bucket"
}

resource "aws_s3_bucket_policy" "secure" {
  bucket = aws_s3_bucket.secure.id

  policy = jsonencode({
    Version = "2012-10-17"
    Statement = [{
      Effect = "Deny"
      Principal = "*"
      Action    = "s3:*"
      Resource  = [
        aws_s3_bucket.secure.arn,
        "${aws_s3_bucket.secure.arn}/*"
      ]
      Condition = {
        Bool = {
          "aws:SecureTransport" = "false"
        }
      }
    }]
  })
}

resource "aws_s3_bucket" "filtered" {
  bucket = "my-filtered-bucket"
}

resource "aws_s3_bucket_notification" "filtered" {
  bucket = aws_s3_bucket.filtered.id

  lambda_function {
    lambda_function_arn = aws_lambda_function.processor.arn
    events              = ["s3:ObjectCreated:*"]
    filter_prefix       = "uploads/"
    filter_suffix       = ".jpg"
  }
}

